document.addEventListener("click",(e=>{const a=e.target;"jarvis-semantic-section"!==a.className&&"jarvis-semantic-note"!==a.className||webviewApi.postMessage({name:"openRelatedNote",note:a.dataset.note,line:a.dataset.line})})),document.addEventListener("search",(e=>{const a=e.target;"jarvis-semantic-query"===a.className&&webviewApi.postMessage({name:"searchRelatedNote",query:a.value})}));